import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { FiSearch, FiFilter, FiMoreVertical, FiEdit, FiTrash2, FiUserPlus, FiMail, FiUser, FiShield } from 'react-icons/fi';
import LoadingSpinner from '../../components/UI/LoadingSpinner';

const UsersContainer = styled.div`
  padding: 2rem;
  max-width: 1400px;
  margin: 0 auto;
`;

const Header = styled.div`
  display: flex;
  justify-content: between;
  align-items: center;
  margin-bottom: 2rem;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 1rem;
    align-items: flex-start;
  }
`;

const Title = styled.h1`
  font-size: 2rem;
  font-weight: 600;
  color: ${props => props.theme.colors.text};
  margin: 0;
`;

const Actions = styled.div`
  display: flex;
  gap: 1rem;
  margin-left: auto;
`;

const AddUserButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  background: ${props => props.theme.colors.primary};
  color: white;
  border: none;
  border-radius: 8px;
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background: ${props => props.theme.colors.primaryDark};
  }
`;

const FiltersContainer = styled.div`
  display: flex;
  gap: 1rem;
  margin-bottom: 2rem;
  padding: 1.5rem;
  background: ${props => props.theme.colors.surface};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: 12px;

  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const SearchInput = styled.input`
  flex: 1;
  padding: 0.75rem;
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: 8px;
  background: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.text};
  font-size: 0.875rem;

  &::placeholder {
    color: ${props => props.theme.colors.textMuted};
  }

  &:focus {
    outline: none;
    border-color: ${props => props.theme.colors.primary};
  }
`;

const FilterSelect = styled.select`
  padding: 0.75rem;
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: 8px;
  background: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.text};
  font-size: 0.875rem;
  min-width: 150px;

  &:focus {
    outline: none;
    border-color: ${props => props.theme.colors.primary};
  }
`;

const UsersTable = styled.div`
  background: ${props => props.theme.colors.surface};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: 12px;
  overflow: hidden;
`;

const TableHeader = styled.div`
  display: grid;
  grid-template-columns: 1fr 200px 150px 120px 100px 60px;
  padding: 1rem 1.5rem;
  background: ${props => props.theme.colors.background};
  border-bottom: 1px solid ${props => props.theme.colors.border};
  font-size: 0.875rem;
  font-weight: 600;
  color: ${props => props.theme.colors.textMuted};

  @media (max-width: 768px) {
    grid-template-columns: 1fr 100px 80px;
  }
`;

const TableRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 200px 150px 120px 100px 60px;
  padding: 1rem 1.5rem;
  border-bottom: 1px solid ${props => props.theme.colors.border};
  transition: all 0.2s ease;

  &:hover {
    background: ${props => props.theme.colors.background};
  }

  &:last-child {
    border-bottom: none;
  }

  @media (max-width: 768px) {
    grid-template-columns: 1fr 100px 80px;
  }
`;

const UserInfo = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const UserAvatar = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: ${props => props.theme.colors.primary};
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: 600;
  font-size: 0.875rem;
`;

const UserDetails = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.25rem;
`;

const UserName = styled.span`
  font-weight: 500;
  color: ${props => props.theme.colors.text};
`;

const UserEmail = styled.span`
  font-size: 0.875rem;
  color: ${props => props.theme.colors.textMuted};
`;

const RoleBadge = styled.span`
  padding: 0.25rem 0.75rem;
  border-radius: 20px;
  font-size: 0.75rem;
  font-weight: 500;
  background: ${props => {
    switch (props.role) {
      case 'admin': return '#DC262620';
      case 'moderator': return '#D9721520';
      case 'user': return '#05966920';
      default: return '#6B728020';
    }
  }};
  color: ${props => {
    switch (props.role) {
      case 'admin': return '#DC2626';
      case 'moderator': return '#D97215';
      case 'user': return '#059669';
      default: return '#6B7280';
    }
  }};
`;

const StatusBadge = styled.span`
  padding: 0.25rem 0.75rem;
  border-radius: 20px;
  font-size: 0.75rem;
  font-weight: 500;
  background: ${props => props.active ? '#10B98120' : '#EF444420'};
  color: ${props => props.active ? '#10B981' : '#EF4444'};
`;

const ActionButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  border: none;
  background: transparent;
  color: ${props => props.theme.colors.textMuted};
  border-radius: 6px;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background: ${props => props.theme.colors.border};
    color: ${props => props.theme.colors.text};
  }
`;

const Pagination = styled.div`
  display: flex;
  justify-content: between;
  align-items: center;
  padding: 1rem 1.5rem;
  background: ${props => props.theme.colors.surface};
  border-top: 1px solid ${props => props.theme.colors.border};
`;

const PaginationInfo = styled.span`
  font-size: 0.875rem;
  color: ${props => props.theme.colors.textMuted};
`;

const PaginationButtons = styled.div`
  display: flex;
  gap: 0.5rem;
  margin-left: auto;
`;

const PaginationButton = styled.button`
  padding: 0.5rem 1rem;
  border: 1px solid ${props => props.theme.colors.border};
  background: ${props => props.active ? props.theme.colors.primary : props.theme.colors.background};
  color: ${props => props.active ? 'white' : props.theme.colors.text};
  border-radius: 6px;
  font-size: 0.875rem;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover:not(:disabled) {
    background: ${props => props.active ? props.theme.colors.primary : props.theme.colors.border};
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const HiddenOnMobile = styled.span`
  @media (max-width: 768px) {
    display: none;
  }
`;

const AdminUsers = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('all');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    fetchUsers();
  }, [currentPage, searchTerm, roleFilter, statusFilter]);

  const fetchUsers = async () => {
    try {
      setLoading(true);

      // Заглушка данных для демонстрации
      const mockUsers = [
        {
          id: 1,
          username: 'admin',
          email: 'admin@uxuilab.com',
          name: 'Администратор',
          role: 'admin',
          isActive: true,
          createdAt: '2024-01-15T10:00:00Z',
          lastActivity: '2024-01-20T14:30:00Z'
        },
        {
          id: 2,
          username: 'demo',
          email: 'demo@uxuilab.com',
          name: 'Демо пользователь',
          role: 'user',
          isActive: true,
          createdAt: '2024-01-16T09:00:00Z',
          lastActivity: '2024-01-20T13:15:00Z'
        },
        {
          id: 3,
          username: 'test',
          email: 'test@example.com',
          name: 'Тестовый пользователь',
          role: 'user',
          isActive: true,
          createdAt: '2024-01-17T11:00:00Z',
          lastActivity: '2024-01-19T16:45:00Z'
        },
        {
          id: 4,
          username: 'moderator1',
          email: 'mod@uxuilab.com',
          name: 'Модератор',
          role: 'moderator',
          isActive: true,
          createdAt: '2024-01-10T08:00:00Z',
          lastActivity: '2024-01-20T12:00:00Z'
        },
        {
          id: 5,
          username: 'inactive_user',
          email: 'inactive@example.com',
          name: 'Неактивный пользователь',
          role: 'user',
          isActive: false,
          createdAt: '2024-01-05T07:00:00Z',
          lastActivity: '2024-01-10T10:00:00Z'
        }
      ];

      // Фильтрация
      let filteredUsers = mockUsers;

      if (searchTerm) {
        filteredUsers = filteredUsers.filter(user => 
          user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
          user.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
          user.username.toLowerCase().includes(searchTerm.toLowerCase())
        );
      }

      if (roleFilter !== 'all') {
        filteredUsers = filteredUsers.filter(user => user.role === roleFilter);
      }

      if (statusFilter !== 'all') {
        filteredUsers = filteredUsers.filter(user => 
          statusFilter === 'active' ? user.isActive : !user.isActive
        );
      }

      setUsers(filteredUsers);
      setTotalPages(Math.ceil(filteredUsers.length / 10));
    } catch (error) {
      console.error('Ошибка загрузки пользователей:', error);
    } finally {
      setLoading(false);
    }
  };

  const getInitials = (name) => {
    return name?.split(' ').map(n => n[0]).join('').toUpperCase() || '?';
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('ru-RU', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  };

  if (loading) {
    return <LoadingSpinner fullScreen />;
  }

  return (
    <UsersContainer>
      <Header>
        <Title>Управление пользователями</Title>
        <Actions>
          <AddUserButton>
            <FiUserPlus />
            Добавить пользователя
          </AddUserButton>
        </Actions>
      </Header>

      <FiltersContainer>
        <SearchInput
          type="text"
          placeholder="Поиск по имени, email или username..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />

        <FilterSelect
          value={roleFilter}
          onChange={(e) => setRoleFilter(e.target.value)}
        >
          <option value="all">Все роли</option>
          <option value="admin">Администратор</option>
          <option value="moderator">Модератор</option>
          <option value="user">Пользователь</option>
        </FilterSelect>

        <FilterSelect
          value={statusFilter}
          onChange={(e) => setStatusFilter(e.target.value)}
        >
          <option value="all">Все статусы</option>
          <option value="active">Активные</option>
          <option value="inactive">Неактивные</option>
        </FilterSelect>
      </FiltersContainer>

      <UsersTable>
        <TableHeader>
          <span>Пользователь</span>
          <HiddenOnMobile>Email</HiddenOnMobile>
          <HiddenOnMobile>Роль</HiddenOnMobile>
          <span>Статус</span>
          <HiddenOnMobile>Дата регистрации</HiddenOnMobile>
          <span>Действия</span>
        </TableHeader>

        {users.map((user) => (
          <TableRow key={user.id}>
            <UserInfo>
              <UserAvatar>
                {getInitials(user.name)}
              </UserAvatar>
              <UserDetails>
                <UserName>{user.name || user.username}</UserName>
                <UserEmail>{user.email}</UserEmail>
              </UserDetails>
            </UserInfo>

            <HiddenOnMobile>{user.email}</HiddenOnMobile>

            <HiddenOnMobile>
              <RoleBadge role={user.role}>
                {user.role === 'admin' ? 'Администратор' :
                 user.role === 'moderator' ? 'Модератор' : 'Пользователь'}
              </RoleBadge>
            </HiddenOnMobile>

            <StatusBadge active={user.isActive}>
              {user.isActive ? 'Активен' : 'Неактивен'}
            </StatusBadge>

            <HiddenOnMobile>{formatDate(user.createdAt)}</HiddenOnMobile>

            <ActionButton>
              <FiMoreVertical />
            </ActionButton>
          </TableRow>
        ))}
      </UsersTable>

      <Pagination>
        <PaginationInfo>
          Показано {users.length} из {users.length} пользователей
        </PaginationInfo>

        <PaginationButtons>
          <PaginationButton
            onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
            disabled={currentPage === 1}
          >
            Назад
          </PaginationButton>

          {Array.from({ length: totalPages }, (_, i) => i + 1).map(page => (
            <PaginationButton
              key={page}
              active={page === currentPage}
              onClick={() => setCurrentPage(page)}
            >
              {page}
            </PaginationButton>
          ))}

          <PaginationButton
            onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
            disabled={currentPage === totalPages}
          >
            Вперёд
          </PaginationButton>
        </PaginationButtons>
      </Pagination>
    </UsersContainer>
  );
};

export default AdminUsers;
