import React, { useState, useRef, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import styled, { useTheme } from 'styled-components';
import { useAuth } from '../../contexts/AuthContext';
import { 
  FiMenu, 
  FiSearch, 
  FiBell, 
  FiUser, 
  FiSettings, 
  FiLogOut,
  FiSun,
  FiMoon,
  FiChevronDown,
  FiCheck,
  FiTrash2
} from 'react-icons/fi';

const HeaderContainer = styled.header`
  height: 70px;
  background: ${props => props.theme.colors.surface};
  border-bottom: 1px solid ${props => props.theme.colors.border};
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 2rem;
  position: sticky;
  top: 0;
  z-index: 100;
  box-shadow: ${props => props.theme.shadows.small};

  @media (max-width: 768px) {
    padding: 0 1rem;
  }
`;

const LeftSection = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const MenuButton = styled.button`
  display: none;
  background: none;
  border: none;
  color: ${props => props.theme.colors.text};
  cursor: pointer;
  padding: 0.5rem;
  border-radius: ${props => props.theme.borderRadius.medium};
  transition: all 0.2s ease;

  &:hover {
    background: ${props => props.theme.colors.surfaceHover};
  }

  @media (max-width: 768px) {
    display: flex;
    align-items: center;
    justify-content: center;
  }
`;

const SearchContainer = styled.div`
  position: relative;
  max-width: 400px;
  flex: 1;

  @media (max-width: 640px) {
    display: none;
  }
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 0.75rem 1rem 0.75rem 2.5rem;
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.medium};
  background: ${props => props.theme.colors.background};
  color: ${props => props.theme.colors.text};
  font-size: 0.875rem;
  transition: all 0.2s ease;

  &::placeholder {
    color: ${props => props.theme.colors.textMuted};
  }

  &:focus {
    outline: none;
    border-color: ${props => props.theme.colors.primary};
    box-shadow: 0 0 0 3px ${props => props.theme.colors.primary}20;
  }
`;

const SearchIcon = styled.div`
  position: absolute;
  left: 0.875rem;
  top: 50%;
  transform: translateY(-50%);
  color: ${props => props.theme.colors.textMuted};
  display: flex;
  align-items: center;
`;

const RightSection = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const IconButton = styled.button`
  position: relative;
  background: none;
  border: none;
  color: ${props => props.theme.colors.text};
  cursor: pointer;
  padding: 0.75rem;
  border-radius: ${props => props.theme.borderRadius.medium};
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  justify-content: center;

  &:hover {
    background: ${props => props.theme.colors.surfaceHover};
  }
`;

const NotificationBadge = styled.span`
  position: absolute;
  top: 0.5rem;
  right: 0.5rem;
  background: ${props => props.theme.colors.error};
  color: white;
  font-size: 0.625rem;
  font-weight: 600;
  padding: 0.125rem 0.375rem;
  border-radius: 10px;
  min-width: 16px;
  height: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const NotificationsDropdown = styled.div`
  position: absolute;
  top: 100%;
  right: 0;
  margin-top: 0.5rem;
  background: ${props => props.theme.colors.surface};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.large};
  box-shadow: ${props => props.theme.shadows.large};
  width: 380px;
  max-height: 500px;
  overflow-y: auto;
  z-index: 1000;
  opacity: ${props => props.isOpen ? 1 : 0};
  visibility: ${props => props.isOpen ? 'visible' : 'hidden'};
  transform: ${props => props.isOpen ? 'translateY(0)' : 'translateY(-10px)'};
  transition: all 0.2s ease;

  @media (max-width: 640px) {
    width: 320px;
    right: -10px;
  }
`;

const NotificationsHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1rem;
  border-bottom: 1px solid ${props => props.theme.colors.border};
`;

const NotificationsTitle = styled.h3`
  margin: 0;
  font-size: 1rem;
  font-weight: 600;
  color: ${props => props.theme.colors.text};
`;

const NotificationsActions = styled.div`
  display: flex;
  gap: 0.5rem;
`;

const SmallIconButton = styled.button`
  background: none;
  border: none;
  color: ${props => props.theme.colors.textSecondary};
  cursor: pointer;
  padding: 0.25rem;
  border-radius: ${props => props.theme.borderRadius.small};
  transition: all 0.2s ease;

  &:hover {
    background: ${props => props.theme.colors.surfaceHover};
    color: ${props => props.theme.colors.text};
  }
`;

const NotificationItem = styled.div`
  padding: 1rem;
  border-bottom: 1px solid ${props => props.theme.colors.border};
  transition: all 0.2s ease;
  position: relative;
  opacity: ${props => props.isRead ? 0.7 : 1};

  &:hover {
    background: ${props => props.theme.colors.surfaceHover};
  }

  &:last-child {
    border-bottom: none;
  }

  ${props => !props.isRead && `
    border-left: 3px solid ${props.theme.colors.primary};
    padding-left: calc(1rem - 3px);
  `}
`;

const NotificationContent = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  gap: 0.75rem;
`;

const NotificationText = styled.div`
  flex: 1;
`;

const NotificationTitle = styled.div`
  font-weight: 600;
  color: ${props => props.theme.colors.text};
  font-size: 0.875rem;
  margin-bottom: 0.25rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const NotificationMessage = styled.div`
  color: ${props => props.theme.colors.textSecondary};
  font-size: 0.8rem;
  line-height: 1.4;
  margin-bottom: 0.5rem;
`;

const NotificationTime = styled.div`
  color: ${props => props.theme.colors.textMuted};
  font-size: 0.75rem;
`;

const NotificationItemActions = styled.div`
  display: flex;
  gap: 0.25rem;
`;

const EmptyNotifications = styled.div`
  padding: 2rem;
  text-align: center;
  color: ${props => props.theme.colors.textSecondary};
`;

const ViewAllButton = styled(Link)`
  display: block;
  padding: 0.75rem 1rem;
  text-align: center;
  color: ${props => props.theme.colors.primary};
  text-decoration: none;
  font-weight: 600;
  font-size: 0.875rem;
  border-top: 1px solid ${props => props.theme.colors.border};
  transition: all 0.2s ease;

  &:hover {
    background: ${props => props.theme.colors.surfaceHover};
  }
`;

const ProfileButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.75rem;
  background: none;
  border: none;
  color: ${props => props.theme.colors.text};
  cursor: pointer;
  padding: 0.5rem;
  border-radius: ${props => props.theme.borderRadius.medium};
  transition: all 0.2s ease;

  &:hover {
    background: ${props => props.theme.colors.surfaceHover};
  }

  @media (max-width: 640px) {
    span {
      display: none;
    }
  }
`;

const Avatar = styled.div`
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background: linear-gradient(135deg, ${props => props.theme.colors.primary}, ${props => props.theme.colors.secondary});
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: 600;
  font-size: 0.875rem;
`;

const Dropdown = styled.div`
  position: absolute;
  top: 100%;
  right: 0;
  margin-top: 0.5rem;
  background: ${props => props.theme.colors.surface};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.large};
  box-shadow: ${props => props.theme.shadows.large};
  min-width: 200px;
  padding: 0.5rem;
  z-index: 1000;
  opacity: ${props => props.isOpen ? 1 : 0};
  visibility: ${props => props.isOpen ? 'visible' : 'hidden'};
  transform: ${props => props.isOpen ? 'translateY(0)' : 'translateY(-10px)'};
  transition: all 0.2s ease;
`;

const DropdownItem = styled.button`
  width: 100%;
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 0.75rem;
  background: none;
  border: none;
  color: ${props => props.theme.colors.text};
  font-size: 0.875rem;
  cursor: pointer;
  border-radius: ${props => props.theme.borderRadius.medium};
  transition: all 0.2s ease;
  text-align: left;

  &:hover {
    background: ${props => props.theme.colors.surfaceHover};
  }

  &.danger {
    color: ${props => props.theme.colors.error};
  }
`;

const UserInfo = styled.div`
  padding: 0.75rem;
  border-bottom: 1px solid ${props => props.theme.colors.border};
  margin-bottom: 0.5rem;
`;

const UserName = styled.div`
  font-weight: 600;
  color: ${props => props.theme.colors.text};
  font-size: 0.875rem;
`;

const UserEmail = styled.div`
  color: ${props => props.theme.colors.textSecondary};
  font-size: 0.75rem;
  margin-top: 0.25rem;
`;

const Header = ({ onToggleSidebar }) => {
  const { user, logout } = useAuth();
  const theme = useTheme();
  const navigate = useNavigate();
  const [profileDropdownOpen, setProfileDropdownOpen] = useState(false);
  const [notificationsDropdownOpen, setNotificationsDropdownOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [notificationsLoading, setNotificationsLoading] = useState(false);

  // Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ð¹
  const loadNotifications = async () => {
    try {
      setNotificationsLoading(true);
      const token = localStorage.getItem('uxui_lab_token');

      const [notificationsResponse, unreadResponse] = await Promise.all([
        fetch('/api/notifications?limit=5', {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch('/api/notifications/unread-count', {
          headers: { 'Authorization': `Bearer ${token}` }
        })
      ]);

      if (notificationsResponse.ok && unreadResponse.ok) {
        const notificationsData = await notificationsResponse.json();
        const unreadData = await unreadResponse.json();

        setNotifications(notificationsData.notifications || []);
        setUnreadCount(unreadData.unread_count || 0);
      }
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ð¹:', error);
    } finally {
      setNotificationsLoading(false);
    }
  };

  // ÐžÑ‚Ð¼ÐµÑ‚Ð¸Ñ‚ÑŒ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ðµ ÐºÐ°Ðº Ð¿Ñ€Ð¾Ñ‡Ð¸Ñ‚Ð°Ð½Ð½Ð¾Ðµ
  const markAsRead = async (notificationId) => {
    try {
      const token = localStorage.getItem('uxui_lab_token');
      const response = await fetch(`/api/notifications/${notificationId}/read`, {
        method: 'PATCH',
        headers: { 'Authorization': `Bearer ${token}` }
      });

      if (response.ok) {
        setNotifications(prev => prev.map(notification => 
          notification.id === notificationId 
            ? { ...notification, is_read: true }
            : notification
        ));
        setUnreadCount(prev => Math.max(0, prev - 1));
      }
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ñ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ñ:', error);
    }
  };

  // ÐžÑ‚Ð¼ÐµÑ‚Ð¸Ñ‚ÑŒ Ð²ÑÐµ ÐºÐ°Ðº Ð¿Ñ€Ð¾Ñ‡Ð¸Ñ‚Ð°Ð½Ð½Ñ‹Ðµ
  const markAllAsRead = async () => {
    try {
      const token = localStorage.getItem('uxui_lab_token');
      const response = await fetch('/api/notifications/mark-all-read', {
        method: 'PATCH',
        headers: { 'Authorization': `Bearer ${token}` }
      });

      if (response.ok) {
        setNotifications(prev => prev.map(notification => 
          ({ ...notification, is_read: true })
        ));
        setUnreadCount(0);
      }
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¾Ð±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ñ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ð¹:', error);
    }
  };

  // Ð£Ð´Ð°Ð»Ð¸Ñ‚ÑŒ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ðµ
  const deleteNotification = async (notificationId) => {
    try {
      const token = localStorage.getItem('uxui_lab_token');
      const response = await fetch(`/api/notifications/${notificationId}`, {
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${token}` }
      });

      if (response.ok) {
        const notification = notifications.find(n => n.id === notificationId);
        setNotifications(prev => prev.filter(n => n.id !== notificationId));
        if (notification && !notification.is_read) {
          setUnreadCount(prev => Math.max(0, prev - 1));
        }
      }
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° ÑƒÐ´Ð°Ð»ÐµÐ½Ð¸Ñ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ñ:', error);
    }
  };

  // ÐŸÐ¾Ð»ÑƒÑ‡Ð¸Ñ‚ÑŒ Ð¸ÐºÐ¾Ð½ÐºÑƒ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ñ
  const getNotificationIcon = (type) => {
    switch (type) {
      case 'project_created': return 'ðŸ“';
      case 'analysis_completed': return 'ðŸ“Š';
      case 'report_ready': return 'ðŸ“„';
      case 'system': return 'ðŸ””';
      default: return 'ðŸ“¢';
    }
  };

  // Ð¤Ð¾Ñ€Ð¼Ð°Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ðµ Ð²Ñ€ÐµÐ¼ÐµÐ½Ð¸
  const formatTime = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = (now - date) / (1000 * 60 * 60);

    if (diffInHours < 1) {
      return 'Ñ‚Ð¾Ð»ÑŒÐºÐ¾ Ñ‡Ñ‚Ð¾';
    } else if (diffInHours < 24) {
      return `${Math.floor(diffInHours)} Ñ‡. Ð½Ð°Ð·Ð°Ð´`;
    } else {
      return date.toLocaleDateString('ru-RU');
    }
  };

  const getInitials = (user) => {
    if (user?.firstName && user?.lastName) {
      return `${user.firstName[0]}${user.lastName[0]}`.toUpperCase();
    }
    return user?.username?.[0]?.toUpperCase() || 'U';
  };

  const handleLogout = async () => {
    setProfileDropdownOpen(false);
    await logout();
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      console.log('ÐŸÐ¾Ð¸ÑÐº:', searchQuery);
    }
  };

  const handleNotificationsToggle = () => {
    setNotificationsDropdownOpen(!notificationsDropdownOpen);
    if (!notificationsDropdownOpen) {
      loadNotifications();
    }
  };

  // Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ð¹ Ð¿Ñ€Ð¸ Ð¼Ð¾Ð½Ñ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ð¸
  useEffect(() => {
    loadNotifications();

    // ÐžÐ±Ð½Ð¾Ð²Ð»ÐµÐ½Ð¸Ðµ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ð¹ ÐºÐ°Ð¶Ð´Ñ‹Ðµ 30 ÑÐµÐºÑƒÐ½Ð´
    const interval = setInterval(loadNotifications, 30000);
    return () => clearInterval(interval);
  }, []);

  return (
    <HeaderContainer>
      <LeftSection>
        <MenuButton onClick={onToggleSidebar}>
          <FiMenu size={20} />
        </MenuButton>

        <SearchContainer>
          <form onSubmit={handleSearch}>
            <SearchInput
              type="text"
              placeholder="ÐŸÐ¾Ð¸ÑÐº Ð¿Ñ€Ð¾ÐµÐºÑ‚Ð¾Ð², Ð°Ð½Ð°Ð»Ð¸Ð·Ð¾Ð²..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <SearchIcon>
              <FiSearch size={16} />
            </SearchIcon>
          </form>
        </SearchContainer>
      </LeftSection>

      <RightSection>
        <IconButton onClick={theme.toggleTheme} title={theme.isDarkMode ? 'Ð¡Ð²ÐµÑ‚Ð»Ð°Ñ Ñ‚ÐµÐ¼Ð°' : 'Ð¢ÐµÐ¼Ð½Ð°Ñ Ñ‚ÐµÐ¼Ð°'}>
          {theme.isDarkMode ? <FiSun size={18} /> : <FiMoon size={18} />}
        </IconButton>

        <div style={{ position: 'relative' }}>
          <IconButton 
            onClick={handleNotificationsToggle}
            title="Ð£Ð²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ñ"
          >
            <FiBell size={18} />
            {unreadCount > 0 && (
              <NotificationBadge>{unreadCount > 99 ? '99+' : unreadCount}</NotificationBadge>
            )}
          </IconButton>

          <NotificationsDropdown isOpen={notificationsDropdownOpen}>
            <NotificationsHeader>
              <NotificationsTitle>Ð£Ð²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ñ</NotificationsTitle>
              <NotificationsActions>
                {unreadCount > 0 && (
                  <SmallIconButton onClick={markAllAsRead} title="ÐžÑ‚Ð¼ÐµÑ‚Ð¸Ñ‚ÑŒ Ð²ÑÐµ ÐºÐ°Ðº Ð¿Ñ€Ð¾Ñ‡Ð¸Ñ‚Ð°Ð½Ð½Ñ‹Ðµ">
                    <FiCheck size={14} />
                  </SmallIconButton>
                )}
              </NotificationsActions>
            </NotificationsHeader>

            {notificationsLoading ? (
              <EmptyNotifications>Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ°...</EmptyNotifications>
            ) : notifications.length === 0 ? (
              <EmptyNotifications>
                <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>ðŸ””</div>
                <div>ÐÐµÑ‚ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ð¹</div>
              </EmptyNotifications>
            ) : (
              <>
                {notifications.map((notification) => (
                  <NotificationItem 
                    key={notification.id}
                    isRead={notification.is_read}
                  >
                    <NotificationContent>
                      <NotificationText>
                        <NotificationTitle>
                          <span>{getNotificationIcon(notification.type)}</span>
                          {notification.title}
                        </NotificationTitle>
                        <NotificationMessage>
                          {notification.message}
                        </NotificationMessage>
                        <NotificationTime>
                          {formatTime(notification.created_at)}
                        </NotificationTime>
                      </NotificationText>

                      <NotificationItemActions>
                        {!notification.is_read && (
                          <SmallIconButton 
                            onClick={() => markAsRead(notification.id)}
                            title="ÐžÑ‚Ð¼ÐµÑ‚Ð¸Ñ‚ÑŒ ÐºÐ°Ðº Ð¿Ñ€Ð¾Ñ‡Ð¸Ñ‚Ð°Ð½Ð½Ð¾Ðµ"
                          >
                            <FiCheck size={12} />
                          </SmallIconButton>
                        )}
                        <SmallIconButton 
                          onClick={() => deleteNotification(notification.id)}
                          title="Ð£Ð´Ð°Ð»Ð¸Ñ‚ÑŒ"
                        >
                          <FiTrash2 size={12} />
                        </SmallIconButton>
                      </NotificationItemActions>
                    </NotificationContent>
                  </NotificationItem>
                ))}

                <ViewAllButton 
                  to="/notifications" 
                  onClick={() => setNotificationsDropdownOpen(false)}
                >
                  ÐŸÐ¾ÑÐ¼Ð¾Ñ‚Ñ€ÐµÑ‚ÑŒ Ð²ÑÐµ ÑƒÐ²ÐµÐ´Ð¾Ð¼Ð»ÐµÐ½Ð¸Ñ
                </ViewAllButton>
              </>
            )}
          </NotificationsDropdown>
        </div>

        <div style={{ position: 'relative' }}>
          <ProfileButton 
            onClick={() => setProfileDropdownOpen(!profileDropdownOpen)}
            title="ÐŸÑ€Ð¾Ñ„Ð¸Ð»ÑŒ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ"
          >
            <Avatar>
              {getInitials(user)}
            </Avatar>
            <span>{user?.firstName || user?.username}</span>
          </ProfileButton>

          <Dropdown isOpen={profileDropdownOpen}>
            <UserInfo>
              <UserName>{user?.firstName || user?.username}</UserName>
              <UserEmail>{user?.email}</UserEmail>
            </UserInfo>

            <DropdownItem onClick={() => {
              setProfileDropdownOpen(false);
              navigate('/profile');
            }}>
              <FiUser size={16} />
              ÐŸÑ€Ð¾Ñ„Ð¸Ð»ÑŒ
            </DropdownItem>

            <DropdownItem onClick={() => {
              setProfileDropdownOpen(false);
              navigate('/settings');
            }}>
              <FiSettings size={16} />
              ÐÐ°ÑÑ‚Ñ€Ð¾Ð¹ÐºÐ¸
            </DropdownItem>

            <DropdownItem className="danger" onClick={handleLogout}>
              <FiLogOut size={16} />
              Ð’Ñ‹Ð¹Ñ‚Ð¸
            </DropdownItem>
          </Dropdown>
        </div>
      </RightSection>

      {/* Ð—Ð°ÐºÑ€Ñ‹Ñ‚Ð¸Ðµ dropdown Ð¿Ñ€Ð¸ ÐºÐ»Ð¸ÐºÐµ Ð²Ð½Ðµ Ð½Ð¸Ñ… */}
      {(profileDropdownOpen || notificationsDropdownOpen) && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            zIndex: 999
          }}
          onClick={() => {
            setProfileDropdownOpen(false);
            setNotificationsDropdownOpen(false);
          }}
        />
      )}
    </HeaderContainer>
  );
};

export default Header;
