const AdminUsers = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState('');
  const [roleFilter, setRoleFilter] = useState('all');
  const [statusFilter, setStatusFilter] = useState('all');
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    loadUsers();
  }, [currentPage, search, roleFilter, statusFilter]);

  const loadUsers = async () => {
    try {
      setLoading(true);

      const params = new URLSearchParams({
        page: currentPage.toString(),
        limit: '12',
        ...(search && { search }),
        ...(roleFilter !== 'all' && { role: roleFilter }),
        ...(statusFilter !== 'all' && { status: statusFilter })
      });

      const response = await fetch(`/api/admin/users?${params}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('uxui_lab_token')}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setUsers(data.users || []);
        setTotalPages(data.pagination?.totalPages || 1);
      } else {
        throw new Error('Ошибка загрузки пользователей');
      }
    } catch (err) {
      console.error('Ошибка загрузки пользователей:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const getUserInitials = (user) => {
    if (user.firstName && user.lastName) {
      return `${user.firstName[0]}${user.lastName[0]}`.toUpperCase();
    }
    return user.username ? user.username[0].toUpperCase() : 'U';
  };

  const getUserName = (user) => {
    if (user.firstName && user.lastName) {
      return `${user.firstName} ${user.lastName}`;
    }
    return user.username || 'Пользователь';
  };

  const getAvatarColor = (role) => {
    switch (role) {
      case 'admin': return '#DC2626';
      case 'moderator': return '#D97706';
      default: return '#059669';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('ru-RU');
  };

  if (loading && users.length === 0) {
    return <LoadingSpinner fullScreen />;
  }

  if (error) {
    return (
      <Container>
        <div style={{ textAlign: 'center', padding: '2rem' }}>
          <h2>Ошибка загрузки данных</h2>
          <p>{error}</p>
          <button onClick={loadUsers}>Попробовать снова</button>
        </div>
      </Container>
    );
  }

  return (
    <Container>
      <Header>
        <Title>Управление пользователями</Title>
        <Controls>
          <SearchBox>
            <SearchIcon>
              <FiSearch />
            </SearchIcon>
            <SearchInput
              type="text"
              placeholder="Поиск пользователей..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
          </SearchBox>

          <FilterSelect
            value={roleFilter}
            onChange={(e) => setRoleFilter(e.target.value)}
          >
            <option value="all">Все роли</option>
            <option value="admin">Администраторы</option>
            <option value="moderator">Модераторы</option>
            <option value="user">Пользователи</option>
          </FilterSelect>

          <FilterSelect
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
          >
            <option value="all">Все статусы</option>
            <option value="active">Активные</option>
            <option value="inactive">Неактивные</option>
          </FilterSelect>
        </Controls>
      </Header>

      {loading ? (
        <div style={{ textAlign: 'center', padding: '2rem' }}>
          <LoadingSpinner />
        </div>
      ) : (
        <>
          <UsersGrid>
            {users.map((user) => (
              <UserCard key={user.id}>
                <UserInfo>
                  <Avatar color={getAvatarColor(user.role)}>
                    {getUserInitials(user)}
                  </Avatar>
                  <UserDetails>
                    <UserName>{getUserName(user)}</UserName>
                    <UserEmail>{user.email}</UserEmail>
                  </UserDetails>
                </UserInfo>

                <UserMeta>
                  <RoleBadge role={user.role}>
                    {user.role === 'admin' ? 'Администратор' : 
                     user.role === 'moderator' ? 'Модератор' : 'Пользователь'}
                  </RoleBadge>
                  <JoinDate>
                    {formatDate(user.createdAt || user.created_at)}
                  </JoinDate>
                </UserMeta>

                <Actions>
                  <ActionButton>
                    <FiEdit2 />
                  </ActionButton>
                  <ActionButton danger>
                    <FiTrash2 />
                  </ActionButton>
                </Actions>
              </UserCard>
            ))}
          </UsersGrid>

          {totalPages > 1 && (
            <Pagination>
              <PageButton
                onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
              >
                Назад
              </PageButton>

              {Array.from({ length: totalPages }, (_, i) => i + 1)
                .filter(page => 
                  page === 1 || 
                  page === totalPages || 
                  Math.abs(page - currentPage) <= 2
                )
                .map(page => (
                  <PageButton
                    key={page}
                    active={page === currentPage}
                    onClick={() => setCurrentPage(page)}
                  >
                    {page}
                  </PageButton>
                ))}

              <PageButton
                onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                disabled={currentPage === totalPages}
              >
                Вперед
              </PageButton>
            </Pagination>
          )}
        </>
      )}
    </Container>
  );
};

export default AdminUsers;
