import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { useAuth } from '../contexts/AuthContext';
import { useTheme } from '../contexts/ThemeContext';
import LoadingSpinner from '../components/UI/LoadingSpinner';
import { 
  FiDownload, FiEye, FiFilter, FiSearch, FiCalendar,
  FiFileText, FiBarChart2, FiTrendingUp, FiShare2,
  FiPrinter, FiTrash2, FiRefreshCw, FiGrid,
  FiList, FiMoreVertical, FiPlus, FiEdit3
} from 'react-icons/fi';

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2rem;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 1rem;
    align-items: stretch;
  }
`;

const Title = styled.h1`
  color: ${props => props.theme.colors.text};
  margin: 0;
  font-size: 2rem;
  font-weight: 700;
`;

const Actions = styled.div`
  display: flex;
  gap: 1rem;
  align-items: center;

  @media (max-width: 768px) {
    flex-direction: column;
    width: 100%;
  }
`;

const Button = styled.button`
  background: ${props => props.variant === 'primary' 
    ? `linear-gradient(135deg, ${props.theme.colors.primary}, ${props.theme.colors.primaryDark})`
    : props.variant === 'success'
    ? `linear-gradient(135deg, ${props.theme.colors.success}, ${props.theme.colors.success}dd)`
    : props.variant === 'danger'
    ? `linear-gradient(135deg, ${props.theme.colors.error}, ${props.theme.colors.error}dd)`
    : props.theme.colors.surface};
  color: ${props => ['primary', 'success', 'danger'].includes(props.variant)
    ? 'white' 
    : props.theme.colors.text};
  border: ${props => ['primary', 'success', 'danger'].includes(props.variant)
    ? 'none'
    : `1px solid ${props.theme.colors.border}`};
  padding: 0.75rem 1.5rem;
  border-radius: ${props => props.theme.borderRadius.medium};
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    transform: translateY(-1px);
    box-shadow: ${props => props.theme.shadows.medium};
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
  }
`;

const Stats = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1.5rem;
  margin-bottom: 2rem;
`;

const StatCard = styled.div`
  background: ${props => props.theme.colors.surface};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.large};
  padding: 1.5rem;
  transition: all 0.2s ease;

  &:hover {
    border-color: ${props => props.theme.colors.primary};
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.medium};
  }
`;

const StatHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 1rem;
`;

const StatIcon = styled.div`
  background: ${props => props.color + '20'};
  color: ${props => props.color};
  padding: 0.75rem;
  border-radius: ${props => props.theme.borderRadius.medium};
  display: flex;
  align-items: center;
  justify-content: center;
`;

const StatValue = styled.div`
  font-size: 2rem;
  font-weight: 700;
  color: ${props => props.theme.colors.text};
  margin-bottom: 0.5rem;
`;

const StatLabel = styled.div`
  color: ${props => props.theme.colors.textSecondary};
  font-size: 0.875rem;
`;

const Filters = styled.div`
  background: ${props => props.theme.colors.surface};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.large};
  padding: 1.5rem;
  margin-bottom: 2rem;
  display: flex;
  gap: 1rem;
  align-items: center;
  flex-wrap: wrap;
`;

const SearchInput = styled.input`
  background: ${props => props.theme.colors.background};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.medium};
  padding: 0.75rem 1rem 0.75rem 2.5rem;
  color: ${props => props.theme.colors.text};
  min-width: 250px;
  position: relative;

  &::placeholder {
    color: ${props => props.theme.colors.textMuted};
  }

  &:focus {
    outline: none;
    border-color: ${props => props.theme.colors.primary};
    box-shadow: 0 0 0 3px ${props => props.theme.colors.primary}20;
  }
`;

const SearchContainer = styled.div`
  position: relative;
  display: flex;
  align-items: center;
`;

const SearchIcon = styled.div`
  position: absolute;
  left: 0.75rem;
  color: ${props => props.theme.colors.textMuted};
  z-index: 1;
`;

const Select = styled.select`
  background: ${props => props.theme.colors.background};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.medium};
  padding: 0.75rem 1rem;
  color: ${props => props.theme.colors.text};
  cursor: pointer;

  &:focus {
    outline: none;
    border-color: ${props => props.theme.colors.primary};
  }
`;

const ViewToggle = styled.div`
  display: flex;
  background: ${props => props.theme.colors.background};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.medium};
  overflow: hidden;
`;

const ViewButton = styled.button`
  background: ${props => props.active 
    ? props.theme.colors.primary 
    : 'transparent'};
  color: ${props => props.active 
    ? 'white' 
    : props.theme.colors.text};
  border: none;
  padding: 0.75rem;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background: ${props => props.active 
      ? props.theme.colors.primaryDark 
      : props.theme.colors.surfaceHover};
  }
`;

const ReportsList = styled.div`
  display: ${props => props.viewMode === 'grid' ? 'grid' : 'flex'};
  grid-template-columns: ${props => props.viewMode === 'grid' ? 'repeat(auto-fill, minmax(350px, 1fr))' : 'none'};
  flex-direction: ${props => props.viewMode === 'grid' ? 'initial' : 'column'};
  gap: ${props => props.viewMode === 'grid' ? '2rem' : '1rem'};
  margin-bottom: 2rem;
`;

const ReportCard = styled.div`
  background: ${props => props.theme.colors.surface};
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.large};
  padding: 1.5rem;
  transition: all 0.2s ease;
  position: relative;

  &:hover {
    border-color: ${props => props.theme.colors.primary};
    transform: translateY(-2px);
    box-shadow: ${props => props.theme.shadows.large};
  }
`;

const ReportHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 1rem;
`;

const ReportInfo = styled.div`
  flex: 1;
`;

const ReportTitle = styled.h3`
  color: ${props => props.theme.colors.text};
  margin: 0 0 0.5rem 0;
  font-size: 1.25rem;
  font-weight: 600;
`;

const ReportProject = styled.span`
  color: ${props => props.theme.colors.textSecondary};
  font-size: 0.875rem;
`;

const ReportType = styled.span`
  background: ${props => {
    switch (props.type) {
      case 'full': return props.theme.colors.primary + '20';
      case 'accessibility': return props.theme.colors.success + '20';
      case 'performance': return props.theme.colors.warning + '20';
      case 'design': return props.theme.colors.info + '20';
      default: return props.theme.colors.textMuted + '20';
    }
  }};
  color: ${props => {
    switch (props.type) {
      case 'full': return props.theme.colors.primary;
      case 'accessibility': return props.theme.colors.success;
      case 'performance': return props.theme.colors.warning;
      case 'design': return props.theme.colors.info;
      default: return props.theme.colors.textMuted;
    }
  }};
  padding: 0.25rem 0.75rem;
  border-radius: ${props => props.theme.borderRadius.small};
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  display: flex;
  align-items: center;
  gap: 0.25rem;
`;

const ReportActions = styled.div`
  display: flex;
  gap: 0.5rem;
  align-items: center;
`;

const IconButton = styled.button`
  background: transparent;
  border: 1px solid ${props => props.theme.colors.border};
  border-radius: ${props => props.theme.borderRadius.small};
  padding: 0.5rem;
  color: ${props => props.theme.colors.text};
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background: ${props => props.theme.colors.surfaceHover};
    border-color: ${props => props.theme.colors.primary};
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const ReportMeta = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: 1rem;
  margin-top: 1rem;
  padding-top: 1rem;
  border-top: 1px solid ${props => props.theme.colors.border};
`;

const MetaItem = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.25rem;
`;

const MetaLabel = styled.span`
  color: ${props => props.theme.colors.textSecondary};
  font-size: 0.75rem;
  text-transform: uppercase;
  font-weight: 600;
`;

const MetaValue = styled.span`
  color: ${props => props.theme.colors.text};
  font-size: 0.875rem;
`;

const ScoreCircle = styled.div`
  position: absolute;
  top: 1rem;
  right: 1rem;
  width: 60px;
  height: 60px;
  border-radius: 50%;
  background: ${props => {
    if (props.score >= 80) return props.theme.colors.success + '20';
    if (props.score >= 60) return props.theme.colors.warning + '20';
    return props.theme.colors.error + '20';
  }};
  color: ${props => {
    if (props.score >= 80) return props.theme.colors.success;
    if (props.score >= 60) return props.theme.colors.warning;
    return props.theme.colors.error;
  }};
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 700;
  font-size: 1rem;
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 4rem 2rem;
  color: ${props => props.theme.colors.textSecondary};
`;

const EmptyIcon = styled.div`
  font-size: 4rem;
  margin-bottom: 1rem;
  opacity: 0.3;
`;

const ReportsPage = () => {
  const { user } = useAuth();
  const theme = useTheme();

  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [stats, setStats] = useState({
    total: 0,
    thisMonth: 0,
    avgScore: 0,
    downloads: 0
  });

  // Фильтры и поиск
  const [searchTerm, setSearchTerm] = useState('');
  const [typeFilter, setTypeFilter] = useState('');
  const [projectFilter, setProjectFilter] = useState('');
  const [viewMode, setViewMode] = useState('grid');

  // Загрузка данных
  const loadReports = async () => {
    try {
      setLoading(true);
      setError(null);

      const token = localStorage.getItem('uxui_lab_token');
      const response = await fetch('/api/reports', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setReports(data.reports || []);

        // Подсчет статистики
        const total = data.reports?.length || 0;
        const now = new Date();
        const thisMonth = data.reports?.filter(r => {
          const reportDate = new Date(r.created_at);
          return reportDate.getMonth() === now.getMonth() && 
                 reportDate.getFullYear() === now.getFullYear();
        }).length || 0;
        const avgScore = total > 0 
          ? Math.round(data.reports.reduce((sum, r) => sum + (r.score || 0), 0) / total)
          : 0;
        const downloads = data.reports?.reduce((sum, r) => sum + (r.download_count || 0), 0) || 0;

        setStats({ total, thisMonth, avgScore, downloads });
      } else {
        throw new Error('Ошибка загрузки отчетов');
      }
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  // Скачивание отчета
  const downloadReport = async (reportId, format = 'pdf') => {
    try {
      const token = localStorage.getItem('uxui_lab_token');
      const response = await fetch(`/api/reports/${reportId}/download?format=${format}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `report-${reportId}.${format}`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);

        // Обновить счетчик скачиваний
        loadReports();
      } else {
        throw new Error('Ошибка скачивания отчета');
      }
    } catch (error) {
      setError(error.message);
    }
  };

  // Удаление отчета
  const deleteReport = async (reportId) => {
    if (!window.confirm('Вы уверены, что хотите удалить этот отчет?')) {
      return;
    }

    try {
      const token = localStorage.getItem('uxui_lab_token');
      const response = await fetch(`/api/reports/${reportId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        loadReports();
      } else {
        throw new Error('Ошибка удаления отчета');
      }
    } catch (error) {
      setError(error.message);
    }
  };

  // Получение иконки типа отчета
  const getTypeIcon = (type) => {
    switch (type) {
      case 'full': return <FiFileText size={14} />;
      case 'accessibility': return <FiEye size={14} />;
      case 'performance': return <FiTrendingUp size={14} />;
      case 'design': return <FiBarChart2 size={14} />;
      default: return <FiFileText size={14} />;
    }
  };

  // Форматирование даты
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('ru-RU', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Фильтрация отчетов
  const filteredReports = reports.filter(report => {
    const matchesSearch = report.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         report.project_name?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = !typeFilter || report.report_type === typeFilter;
    const matchesProject = !projectFilter || report.project_id?.toString() === projectFilter;

    return matchesSearch && matchesType && matchesProject;
  });

  useEffect(() => {
    loadReports();
  }, []);

  return (
    <Container>
      <Header>
        <Title>Отчеты</Title>
        <Actions>
          <ViewToggle>
            <ViewButton 
              active={viewMode === 'grid'} 
              onClick={() => setViewMode('grid')}
            >
              <FiGrid size={18} />
            </ViewButton>
            <ViewButton 
              active={viewMode === 'list'} 
              onClick={() => setViewMode('list')}
            >
              <FiList size={18} />
            </ViewButton>
          </ViewToggle>
          <Button onClick={loadReports}>
            <FiRefreshCw size={18} />
            Обновить
          </Button>
        </Actions>
      </Header>

      {/* Статистика */}
      <Stats>
        <StatCard>
          <StatHeader>
            <div>
              <StatValue>{stats.total}</StatValue>
              <StatLabel>Всего отчетов</StatLabel>
            </div>
            <StatIcon color={theme.colors.primary}>
              <FiFileText size={20} />
            </StatIcon>
          </StatHeader>
        </StatCard>

        <StatCard>
          <StatHeader>
            <div>
              <StatValue>{stats.thisMonth}</StatValue>
              <StatLabel>За этот месяц</StatLabel>
            </div>
            <StatIcon color={theme.colors.success}>
              <FiCalendar size={20} />
            </StatIcon>
          </StatHeader>
        </StatCard>

        <StatCard>
          <StatHeader>
            <div>
              <StatValue>{stats.avgScore}</StatValue>
              <StatLabel>Средняя оценка</StatLabel>
            </div>
            <StatIcon color={theme.colors.warning}>
              <FiBarChart2 size={20} />
            </StatIcon>
          </StatHeader>
        </StatCard>

        <StatCard>
          <StatHeader>
            <div>
              <StatValue>{stats.downloads}</StatValue>
              <StatLabel>Скачиваний</StatLabel>
            </div>
            <StatIcon color={theme.colors.info}>
              <FiDownload size={20} />
            </StatIcon>
          </StatHeader>
        </StatCard>
      </Stats>

      {/* Фильтры */}
      <Filters>
        <SearchContainer>
          <SearchIcon>
            <FiSearch size={18} />
          </SearchIcon>
          <SearchInput
            type="text"
            placeholder="Поиск отчетов..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </SearchContainer>

        <Select 
          value={typeFilter} 
          onChange={(e) => setTypeFilter(e.target.value)}
        >
          <option value="">Все типы</option>
          <option value="full">Полный отчет</option>
          <option value="accessibility">Доступность</option>
          <option value="performance">Производительность</option>
          <option value="design">Дизайн</option>
        </Select>

        <Select 
          value={projectFilter} 
          onChange={(e) => setProjectFilter(e.target.value)}
        >
          <option value="">Все проекты</option>
          {/* Здесь будут проекты из API */}
        </Select>
      </Filters>

      {loading && <LoadingSpinner />}

      {error && (
        <div style={{ color: theme.colors.error, textAlign: 'center', margin: '2rem 0' }}>
          {error}
        </div>
      )}

      {!loading && !error && filteredReports.length === 0 && (
        <EmptyState>
          <EmptyIcon>📄</EmptyIcon>
          <h3>Нет отчетов</h3>
          <p>Создайте первый анализ, чтобы получить отчет</p>
        </EmptyState>
      )}

      {!loading && !error && filteredReports.length > 0 && (
        <ReportsList viewMode={viewMode}>
          {filteredReports.map((report) => (
            <ReportCard key={report.id}>
              {report.score && (
                <ScoreCircle score={report.score}>
                  {report.score}
                </ScoreCircle>
              )}

              <ReportHeader>
                <ReportInfo>
                  <ReportTitle>{report.name || `Отчет #${report.id}`}</ReportTitle>
                  <ReportProject>Проект: {report.project_name || 'Неизвестен'}</ReportProject>
                </ReportInfo>
                <ReportActions>
                  <ReportType type={report.report_type}>
                    {getTypeIcon(report.report_type)}
                    {report.report_type === 'full' ? 'Полный' :
                     report.report_type === 'accessibility' ? 'Доступность' :
                     report.report_type === 'performance' ? 'Производительность' :
                     report.report_type === 'design' ? 'Дизайн' : 'Отчет'}
                  </ReportType>
                </ReportActions>
              </ReportHeader>

              <ReportMeta>
                <MetaItem>
                  <MetaLabel>Дата создания</MetaLabel>
                  <MetaValue>{formatDate(report.created_at)}</MetaValue>
                </MetaItem>
                <MetaItem>
                  <MetaLabel>Размер файла</MetaLabel>
                  <MetaValue>{report.file_size ? `${Math.round(report.file_size / 1024)}KB` : '—'}</MetaValue>
                </MetaItem>
                <MetaItem>
                  <MetaLabel>Скачиваний</MetaLabel>
                  <MetaValue>{report.download_count || 0}</MetaValue>
                </MetaItem>
                <MetaItem>
                  <MetaLabel>Формат</MetaLabel>
                  <MetaValue>{report.format?.toUpperCase() || 'PDF'}</MetaValue>
                </MetaItem>
              </ReportMeta>

              <div style={{ 
                display: 'flex', 
                gap: '0.5rem', 
                marginTop: '1rem',
                justifyContent: 'flex-end'
              }}>
                <IconButton title="Просмотр">
                  <FiEye size={16} />
                </IconButton>
                <IconButton 
                  title="Скачать PDF"
                  onClick={() => downloadReport(report.id, 'pdf')}
                >
                  <FiDownload size={16} />
                </IconButton>
                <IconButton title="Поделиться">
                  <FiShare2 size={16} />
                </IconButton>
                <IconButton title="Печать">
                  <FiPrinter size={16} />
                </IconButton>
                <IconButton 
                  title="Удалить"
                  onClick={() => deleteReport(report.id)}
                >
                  <FiTrash2 size={16} />
                </IconButton>
              </div>
            </ReportCard>
          ))}
        </ReportsList>
      )}
    </Container>
  );
};

export default ReportsPage;
