const Header = ({ onToggleSidebar }) => {
  const { user, logout } = useAuth();
  const { theme, toggleTheme, isDarkMode } = useTheme();
  const [profileDropdownOpen, setProfileDropdownOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');

  const getInitials = (user) => {
    if (user?.firstName && user?.lastName) {
      return `${user.firstName[0]}${user.lastName[0]}`.toUpperCase();
    }
    return user?.username?.[0]?.toUpperCase() || 'U';
  };

  const handleLogout = async () => {
    setProfileDropdownOpen(false);
    await logout();
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      // Здесь будет логика поиска
      console.log('Поиск:', searchQuery);
    }
  };

  return (
    <HeaderContainer>
      <LeftSection>
        <MenuButton onClick={onToggleSidebar}>
          <FiMenu size={20} />
        </MenuButton>

        <SearchContainer>
          <form onSubmit={handleSearch}>
            <SearchInput
              type="text"
              placeholder="Поиск проектов, анализов..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <SearchIcon>
              <FiSearch size={16} />
            </SearchIcon>
          </form>
        </SearchContainer>
      </LeftSection>

      <RightSection>
        <IconButton onClick={toggleTheme} title={isDarkMode ? 'Светлая тема' : 'Темная тема'}>
          {isDarkMode ? <FiSun size={18} /> : <FiMoon size={18} />}
        </IconButton>

        <IconButton title="Уведомления">
          <FiBell size={18} />
          <NotificationBadge>3</NotificationBadge>
        </IconButton>

        <div style={{ position: 'relative' }}>
          <ProfileButton 
            onClick={() => setProfileDropdownOpen(!profileDropdownOpen)}
            title="Профиль пользователя"
          >
            <Avatar>
              {getInitials(user)}
            </Avatar>
            <span>{user?.firstName || user?.username}</span>
          </ProfileButton>

          <Dropdown isOpen={profileDropdownOpen}>
            <UserInfo>
              <UserName>{user?.firstName || user?.username}</UserName>
              <UserEmail>{user?.email}</UserEmail>
            </UserInfo>

            <DropdownItem onClick={() => setProfileDropdownOpen(false)}>
              <FiUser size={16} />
              Профиль
            </DropdownItem>

            <DropdownItem onClick={() => setProfileDropdownOpen(false)}>
              <FiSettings size={16} />
              Настройки
            </DropdownItem>

            <DropdownItem className="danger" onClick={handleLogout}>
              <FiLogOut size={16} />
              Выйти
            </DropdownItem>
          </Dropdown>
        </div>
      </RightSection>

      {/* Закрытие dropdown при клике вне его */}
      {profileDropdownOpen && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            zIndex: 999
          }}
          onClick={() => setProfileDropdownOpen(false)}
        />
      )}
    </HeaderContainer>
  );
};

export default Header;
