import React from 'react';
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';
import { useAuth } from '../../contexts/AuthContext';
import {
  FiHome,
  FiFolder,
  FiBarChart2,
  FiFileText,
  FiSettings,
  FiUser,
  FiX,
  FiTarget
} from 'react-icons/fi';

const SidebarContainer = styled.aside`
  position: fixed;
  top: 0;
  left: 0;
  height: 100vh;
  width: ${props => props.isOpen ? '280px' : '80px'};
  background: ${props => props.theme.colors.surface};
  border-right: 1px solid ${props => props.theme.colors.border};
  z-index: ${props => props.theme.zIndex.modal};
  transition: width 0.3s ease;
  overflow: hidden;
  display: flex;
  flex-direction: column;

  @media (max-width: 768px) {
    width: ${props => props.isOpen ? '280px' : '0'};
    opacity: ${props => props.isOpen ? 1 : 0};
    visibility: ${props => props.isOpen ? 'visible' : 'hidden'};
  }
`;

const Overlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  z-index: ${props => props.theme.zIndex.modal - 1};
  opacity: ${props => props.show ? 1 : 0};
  visibility: ${props => props.show ? 'visible' : 'hidden'};
  transition: all 0.3s ease;

  @media (min-width: 769px) {
    display: none;
  }
`;

const SidebarHeader = styled.div`
  height: 70px;
  display: flex;
  align-items: center;
  justify-content: ${props => props.isOpen ? 'space-between' : 'center'};
  padding: ${props => props.isOpen ? '0 1.5rem' : '0'};
  border-bottom: 1px solid ${props => props.theme.colors.border};
`;

const Logo = styled.div`
  display: flex;
  align-items: center;
  gap: 0.75rem;
  color: ${props => props.theme.colors.primary};
  font-weight: 700;
  font-size: 1.25rem;
  white-space: nowrap;
  opacity: ${props => props.isOpen ? 1 : 0};
  transition: opacity 0.3s ease;

  @media (max-width: 768px) {
    opacity: 1;
  }
`;

const LogoIcon = styled.div`
  width: 40px;
  height: 40px;
  background: linear-gradient(135deg, ${props => props.theme.colors.primary}, ${props => props.theme.colors.secondary});
  border-radius: ${props => props.theme.borderRadius.medium};
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  flex-shrink: 0;
`;

const CloseButton = styled.button`
  display: none;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  background: transparent;
  border-radius: ${props => props.theme.borderRadius.medium};
  color: ${props => props.theme.colors.textMuted};
  transition: all 0.2s ease;

  &:hover {
    background: ${props => props.theme.colors.gray100};
    color: ${props => props.theme.colors.text};
  }

  @media (max-width: 768px) {
    display: flex;
  }
`;

const Navigation = styled.nav`
  flex: 1;
  padding: 1.5rem 0;
  overflow-y: auto;
`;

const NavSection = styled.div`
  margin-bottom: 2rem;

  &:last-child {
    margin-bottom: 0;
  }
`;

const NavSectionTitle = styled.h3`
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.05em;
  color: ${props => props.theme.colors.textMuted};
  margin: 0 1.5rem 0.75rem;
  opacity: ${props => props.isOpen ? 1 : 0};
  transition: opacity 0.3s ease;

  @media (max-width: 768px) {
    opacity: 1;
  }
`;

const NavItem = styled(NavLink).withConfig({
  shouldForwardProp: (prop) => !['isOpen'].includes(prop),
})`
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 0.75rem 1.5rem;
  color: ${props => props.theme.colors.textSecondary};
  text-decoration: none;
  font-weight: 500;
  transition: all 0.2s ease;
  position: relative;
  white-space: nowrap;

  &:hover {
    background: ${props => props.theme.colors.surfaceHover};
    color: ${props => props.theme.colors.text};
  }

  &.active {
    background: ${props => props.theme.colors.primary}15;
    color: ${props => props.theme.colors.primary};
    border-right: 3px solid ${props => props.theme.colors.primary};

    &::before {
      content: '';
      position: absolute;
      left: 0;
      top: 0;
      bottom: 0;
      width: 3px;
      background: ${props => props.theme.colors.primary};
    }
  }

  svg {
    flex-shrink: 0;
  }

  span {
    opacity: ${props => props.isOpen ? 1 : 0};
    transition: opacity 0.3s ease;

    @media (max-width: 768px) {
      opacity: 1;
    }
  }

  ${props => !props.isOpen && `
    justify-content: center;
    padding-left: 0;
    padding-right: 0;
  `}
`;

const UserInfo = styled.div`
  padding: 1rem 1.5rem;
  border-top: 1px solid ${props => props.theme.colors.border};
  display: flex;
  align-items: center;
  gap: 0.75rem;
  opacity: ${props => props.isOpen ? 1 : 0};
  transition: opacity 0.3s ease;

  @media (max-width: 768px) {
    opacity: 1;
  }
`;

const UserAvatar = styled.div`
  width: 32px;
  height: 32px;
  border-radius: 50%;
  background: ${props => props.theme.colors.primary};
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: 600;
  font-size: 0.875rem;
  flex-shrink: 0;
`;

const UserDetails = styled.div`
  flex: 1;
  min-width: 0;
`;

const UserName = styled.div`
  font-weight: 600;
  font-size: 0.875rem;
  color: ${props => props.theme.colors.text};
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
`;

const UserRole = styled.div`
  font-size: 0.75rem;
  color: ${props => props.theme.colors.textMuted};
  text-transform: capitalize;
`;

const Sidebar = ({ isOpen, isMobile, onClose }) => {
  const { user } = useAuth();

  const navigationItems = [
    {
      section: 'Основное',
      items: [
        { to: '/dashboard', icon: FiHome, label: 'Панель управления' },
        { to: '/projects', icon: FiFolder, label: 'Проекты' },
        { to: '/analysis', icon: FiBarChart2, label: 'Анализ' },
        { to: '/reports', icon: FiFileText, label: 'Отчеты' },
      ]
    },
    {
      section: 'Пользователь',
      items: [
        { to: '/profile', icon: FiUser, label: 'Профиль' },
        { to: '/settings', icon: FiSettings, label: 'Настройки' },
      ]
    }
  ];

  const getUserInitials = () => {
    if (user?.firstName && user?.lastName) {
      return `${user.firstName[0]}${user.lastName[0]}`.toUpperCase();
    }
    return user?.username ? user.username[0].toUpperCase() : 'U';
  };

  const getUserDisplayName = () => {
    if (user?.firstName && user?.lastName) {
      return `${user.firstName} ${user.lastName}`;
    }
    return user?.username || 'Пользователь';
  };

  return (
    <>
      <Overlay show={isMobile && isOpen} onClick={onClose} />

      <SidebarContainer isOpen={isOpen}>
        <SidebarHeader isOpen={isOpen}>
          <Logo isOpen={isOpen}>
            <LogoIcon>
              <FiTarget size={20} />
            </LogoIcon>
            <span>UX/UI Lab</span>
          </Logo>

          <CloseButton onClick={onClose}>
            <FiX size={18} />
          </CloseButton>
        </SidebarHeader>

        <Navigation>
          {navigationItems.map((section, sectionIndex) => (
            <NavSection key={sectionIndex}>
              <NavSectionTitle isOpen={isOpen}>
                {section.section}
              </NavSectionTitle>

              {section.items.map((item) => (
                <NavItem
                  key={item.to}
                  to={item.to}
                  isOpen={isOpen}
                  className={({ isActive }) => isActive ? 'active' : ''}
                  onClick={isMobile ? onClose : undefined}
                  title={!isOpen ? item.label : undefined}
                >
                  <item.icon size={20} />
                  <span>{item.label}</span>
                </NavItem>
              ))}
            </NavSection>
          ))}
        </Navigation>

        <UserInfo isOpen={isOpen}>
          <UserAvatar>
            {getUserInitials()}
          </UserAvatar>
          <UserDetails>
            <UserName>{getUserDisplayName()}</UserName>
            <UserRole>{user?.role || 'user'}</UserRole>
          </UserDetails>
        </UserInfo>
      </SidebarContainer>
    </>
  );
};

export default Sidebar;
