const ProfilePage = () => {
  const { user, updateUser, changePassword } = useAuth();
  const theme = useTheme();

  const [loading, setLoading] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [stats, setStats] = useState({
    projects: 0,
    analyses: 0,
    reports: 0,
    joinDate: null
  });

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    bio: '',
    company: '',
    position: '',
    phone: ''
  });

  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  // Загрузка данных профиля
  useEffect(() => {
    if (user) {
      setFormData({
        name: user.name || '',
        email: user.email || '',
        bio: user.bio || '',
        company: user.company || '',
        position: user.position || '',
        phone: user.phone || ''
      });

      // Загрузка статистики
      loadStats();
    }
  }, [user]);

  const loadStats = async () => {
    try {
      const token = localStorage.getItem('uxui_lab_token');
      const [projectsResponse, analysesResponse, reportsResponse] = await Promise.all([
        fetch('/api/projects?limit=1000', {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch('/api/analysis?limit=1000', {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch('/api/reports?limit=1000', {
          headers: { 'Authorization': `Bearer ${token}` }
        })
      ]);

      const [projectsData, analysesData, reportsData] = await Promise.all([
        projectsResponse.json(),
        analysesResponse.json(),
        reportsResponse.json()
      ]);

      setStats({
        projects: projectsData.pagination?.total || 0,
        analyses: analysesData.pagination?.total || 0,
        reports: reportsData.pagination?.total || 0,
        joinDate: user?.created_at
      });
    } catch (error) {
      console.error('Ошибка загрузки статистики:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSaveProfile = async () => {
    try {
      setLoading(true);
      setErrorMessage('');
      setSuccessMessage('');

      const result = await updateUser(formData);

      if (result.success) {
        setSuccessMessage('Профиль успешно обновлен');
        setEditMode(false);
        setTimeout(() => setSuccessMessage(''), 3000);
      } else {
        setErrorMessage(result.error || 'Ошибка обновления профиля');
      }
    } catch (error) {
      setErrorMessage('Произошла ошибка при сохранении');
    } finally {
      setLoading(false);
    }
  };

  const handleChangePassword = async () => {
    try {
      setLoading(true);
      setErrorMessage('');
      setSuccessMessage('');

      if (passwordData.newPassword !== passwordData.confirmPassword) {
        setErrorMessage('Пароли не совпадают');
        return;
      }

      if (passwordData.newPassword.length < 6) {
        setErrorMessage('Пароль должен содержать минимум 6 символов');
        return;
      }

      const result = await changePassword({
        currentPassword: passwordData.currentPassword,
        newPassword: passwordData.newPassword
      });

      if (result.success) {
        setSuccessMessage('Пароль успешно изменен');
        setPasswordData({
          currentPassword: '',
          newPassword: '',
          confirmPassword: ''
        });
        setTimeout(() => setSuccessMessage(''), 3000);
      } else {
        setErrorMessage(result.error || 'Ошибка смены пароля');
      }
    } catch (error) {
      setErrorMessage('Произошла ошибка при смене пароля');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '—';
    return new Date(dateString).toLocaleDateString('ru-RU', {
      day: '2-digit',
      month: 'long',
      year: 'numeric'
    });
  };

  if (!user) {
    return <LoadingSpinner fullScreen />;
  }

  return (
    <Container>
      <Header>
        <Title>Мой профиль</Title>
        <Button 
          variant="primary" 
          onClick={() => setEditMode(!editMode)}
          disabled={loading}
        >
          {editMode ? <FiX size={18} /> : <FiEdit3 size={18} />}
          {editMode ? 'Отменить' : 'Редактировать'}
        </Button>
      </Header>

      {successMessage && (
        <SuccessMessage>
          <FiCheck size={18} />
          {successMessage}
        </SuccessMessage>
      )}

      {errorMessage && (
        <ErrorMessage>
          <FiX size={18} />
          {errorMessage}
        </ErrorMessage>
      )}

      <ProfileContent>
        {/* Левая колонка - Карточка профиля */}
        <ProfileCard>
          <Avatar>
            {user.avatar ? (
              <img src={user.avatar} alt="Avatar" style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
            ) : (
              user.name?.charAt(0)?.toUpperCase() || 'U'
            )}
            <AvatarButton htmlFor="avatar-upload">
              <FiCamera size={16} />
            </AvatarButton>
            <AvatarUpload
              id="avatar-upload"
              type="file"
              accept="image/*"
              // onChange={handleAvatarUpload}
            />
          </Avatar>

          <UserName>{user.name || 'Пользователь'}</UserName>
          <UserEmail>{user.email}</UserEmail>
          <UserRole>
            <FiShield size={14} style={{ marginRight: '0.25rem' }} />
            {user.role === 'admin' ? 'Администратор' : 'Пользователь'}
          </UserRole>

          <UserStats>
            <StatItem>
              <StatValue>{stats.projects}</StatValue>
              <StatLabel>Проектов</StatLabel>
            </StatItem>
            <StatItem>
              <StatValue>{stats.analyses}</StatValue>
              <StatLabel>Анализов</StatLabel>
            </StatItem>
            <StatItem>
              <StatValue>{stats.reports}</StatValue>
              <StatLabel>Отчетов</StatLabel>
            </StatItem>
            <StatItem>
              <StatValue>{formatDate(stats.joinDate)}</StatValue>
              <StatLabel>Дата регистрации</StatLabel>
            </StatItem>
          </UserStats>
        </ProfileCard>

        {/* Правая колонка - Информация и настройки */}
        <InfoSection>
          {/* Основная информация */}
          <Section>
            <SectionHeader>
              <SectionTitle>
                <FiUser size={20} />
                Основная информация
              </SectionTitle>
              {editMode && (
                <Button 
                  variant="success"
                  onClick={handleSaveProfile}
                  disabled={loading}
                >
                  {loading ? <LoadingSpinner size="small" /> : <FiSave size={16} />}
                  Сохранить
                </Button>
              )}
            </SectionHeader>

            <FormGrid>
              <FormGroup>
                <Label htmlFor="name">Имя</Label>
                <Input
                  id="name"
                  name="name"
                  type="text"
                  value={formData.name}
                  onChange={handleInputChange}
                  disabled={!editMode}
                  placeholder="Введите имя"
                />
              </FormGroup>

              <FormGroup>
                <Label htmlFor="email">Email</Label>
                <Input
                  id="email"
                  name="email"
                  type="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  disabled={!editMode}
                  placeholder="Введите email"
                />
              </FormGroup>

              <FormGroup>
                <Label htmlFor="company">Компания</Label>
                <Input
                  id="company"
                  name="company"
                  type="text"
                  value={formData.company}
                  onChange={handleInputChange}
                  disabled={!editMode}
                  placeholder="Введите название компании"
                />
              </FormGroup>

              <FormGroup>
                <Label htmlFor="position">Должность</Label>
                <Input
                  id="position"
                  name="position"
                  type="text"
                  value={formData.position}
                  onChange={handleInputChange}
                  disabled={!editMode}
                  placeholder="Введите должность"
                />
              </FormGroup>

              <FormGroup>
                <Label htmlFor="phone">Телефон</Label>
                <Input
                  id="phone"
                  name="phone"
                  type="tel"
                  value={formData.phone}
                  onChange={handleInputChange}
                  disabled={!editMode}
                  placeholder="+7 (999) 123-45-67"
                />
              </FormGroup>

              <FormGroup style={{ gridColumn: '1 / -1' }}>
                <Label htmlFor="bio">О себе</Label>
                <TextArea
                  id="bio"
                  name="bio"
                  value={formData.bio}
                  onChange={handleInputChange}
                  disabled={!editMode}
                  placeholder="Расскажите о себе..."
                />
              </FormGroup>
            </FormGrid>
          </Section>

          {/* Статистика активности */}
          <Section>
            <SectionHeader>
              <SectionTitle>
                <FiBarChart2 size={20} />
                Статистика активности
              </SectionTitle>
            </SectionHeader>

            <StatsGrid>
              <StatCard>
                <StatIcon color={theme.colors.primary}>
                  <FiUser size={20} />
                </StatIcon>
                <StatNumber>{stats.projects}</StatNumber>
                <StatText>Всего проектов</StatText>
              </StatCard>

              <StatCard>
                <StatIcon color={theme.colors.success}>
                  <FiBarChart2 size={20} />
                </StatIcon>
                <StatNumber>{stats.analyses}</StatNumber>
                <StatText>Выполнено анализов</StatText>
              </StatCard>

              <StatCard>
                <StatIcon color={theme.colors.warning}>
                  <FiTrendingUp size={20} />
                </StatIcon>
                <StatNumber>{stats.reports}</StatNumber>
                <StatText>Создано отчетов</StatText>
              </StatCard>

              <StatCard>
                <StatIcon color={theme.colors.info}>
                  <FiAward size={20} />
                </StatIcon>
                <StatNumber>
                  {user.created_at ? Math.floor((Date.now() - new Date(user.created_at)) / (1000 * 60 * 60 * 24)) : 0}
                </StatNumber>
                <StatText>Дней с нами</StatText>
              </StatCard>
            </StatsGrid>
          </Section>

          {/* Безопасность */}
          <Section>
            <SectionHeader>
              <SectionTitle>
                <FiLock size={20} />
                Безопасность
              </SectionTitle>
              <Button 
                variant="success"
                onClick={handleChangePassword}
                disabled={loading || !passwordData.currentPassword || !passwordData.newPassword || !passwordData.confirmPassword}
              >
                {loading ? <LoadingSpinner size="small" /> : <FiSave size={16} />}
                Изменить пароль
              </Button>
            </SectionHeader>

            <FormGrid>
              <FormGroup>
                <Label htmlFor="currentPassword">Текущий пароль</Label>
                <PasswordContainer>
                  <Input
                    id="currentPassword"
                    name="currentPassword"
                    type={showPassword ? 'text' : 'password'}
                    value={passwordData.currentPassword}
                    onChange={handlePasswordChange}
                    placeholder="Введите текущий пароль"
                  />
                  <PasswordToggle
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? <FiEyeOff size={16} /> : <FiEye size={16} />}
                  </PasswordToggle>
                </PasswordContainer>
              </FormGroup>

              <FormGroup>
                <Label htmlFor="newPassword">Новый пароль</Label>
                <Input
                  id="newPassword"
                  name="newPassword"
                  type="password"
                  value={passwordData.newPassword}
                  onChange={handlePasswordChange}
                  placeholder="Введите новый пароль"
                />
              </FormGroup>

              <FormGroup>
                <Label htmlFor="confirmPassword">Подтвердите пароль</Label>
                <Input
                  id="confirmPassword"
                  name="confirmPassword"
                  type="password"
                  value={passwordData.confirmPassword}
                  onChange={handlePasswordChange}
                  placeholder="Подтвердите новый пароль"
                />
              </FormGroup>
            </FormGrid>
          </Section>
        </InfoSection>
      </ProfileContent>
    </Container>
  );
};

export default ProfilePage;
